    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        
        int sexo=0,sordo=0,oral=0,leer=0;
        String name = jTextField1.getText();
        String aP = jTextField2.getText();
        String aM = jTextField3.getText();
        String curp1 = jTextField4.getText();
        String rfc1 = jTextField5.getText();
        String fechNac = jTextField6.getText();
        String direc = jTextField7.getText();
        String tel = jTextField8.getText();
        String mail = jTextField9.getText();
        String school = (String) jComboBox1.getSelectedItem();
        String lsm = (String) jComboBox2.getSelectedItem();
        String tipoEst = (String) jComboBox3.getSelectedItem();      
        
        if(jRadioButton1.isSelected()==true){
            sexo=1;
            }
        if(jRadioButton3.isSelected()==true){
            leer=1;
            }
        if(jRadioButton5.isSelected()==true){
            oral=1;
            }
        if(jRadioButton7.isSelected()==true){
            sordo=1;
            }
        
        Connection cn = null;
        Statement s;
        try{
            Conexion cx = new Conexion();
            String sql = "INSERT INTO persona (rfc,curp,nombre,aPaterno,aMaterno,fecha_nac,telefono,sexo,escolaridad,sordo,oralizado,nivelLsm,eMail,direccion,tipoEstudio) VALUES('"+rfc1+"', '"+curp1+"', "
                    + "'"+name+"', '"+aP+"', '"+aM+"', '"+fechNac+"'"
                    + ", '"+tel+"', '"+sexo+"','"+school+"', "
                    + " '"+sordo+"','"+oral+"','"+lsm+"','"+mail+"','"+direc+"','"+tipoEst+"');";
            
            cn = Conexion.Enlace(cn);
            s = cn.createStatement();
            s.executeQuery(sql);
            s.close();
            cn.close();
        }catch(SQLException e){
            System.out.println("Error de registro - "+e.getMessage());
        }
        
        Opcion op = new Opcion();
        op.setVisible(true);      
        this.dispose(); 
        
    }//GEN-LAST:event_jButton1ActionPerformed

